	public 	int findSubset(int[] ns){
		Arrays.sort(ns);
		N = ns.length;

		map = new HashMap<Integer, Integer>();
		for(int i = 0; i < N; i++){
			if(!map.containsKey(ns[i])){
				map.put(ns[i], 0);
			}
			int num = map.get(ns[i]) + 1;
			map.put(ns[i], num);
		}
		int M = map.keySet().size();
		int idx = 0;
		ms = new int[M];
		for(int k : map.keySet()){
			ms[idx++] = k;
		}
		Arrays.sort(ms);
		
		int ans = 0;
		if(ms[0] != 1){
			return 0;
		}
		for(int i = 1; i <= map.get(1); i++){
			ans += get(1, i, 1);
			if(i != 1){
				ans += 1;
			}
		}
		return ans;
	}
	
	int get(int idx, int sum, int mul){
		if(idx >= ms.length){
			return 0;
		}

		
		int	ans = get(idx+1, sum, mul);
			
		int next = ms[idx];
		int tmp = 1;
		
		for(int i = 1; i <= map.get(next); i++){
			tmp *= next;
			if(sum + next * i <= mul * tmp){
				break;
			}
			ans++;
			if(idx + 1 < ms.length && sum + next * i + ms[idx+1] > mul * tmp * ms[idx+1]){
				ans += get(idx+1, sum + next * i, mul * tmp);
			}
		}
		
		
		return ans;
	}
	
	
